#[stable(feature = "stdarch_x86_avx512", since = "1.89")]
#[cfg_attr(test, assert_instr(xor))] // generate normal and code instead of kortestw
pub fn _mm512_kortestz(a: __mmask16, b: __mmask16) -> i32 {
    let r = (a | b) == 0;
    r as i32
}

