#[rustc_legacy_const_generics(2)]
#[cfg_attr(test, assert_instr(vpcmp, IMM3 = 0))]
pub fn _mm512_cmp_epi32_mask<const IMM3: _MM_CMPINT_ENUM>(a: __m512i, b: __m512i) -> __mmask16 {
    unsafe {
        static_assert_uimm_bits!(IMM3, 3);
        let a = a.as_i32x16();
        let b = b.as_i32x16();
        let r = match IMM3 {
            0 => simd_eq(a, b),
            1 => simd_lt(a, b),
            2 => simd_le(a, b),
            3 => i32x16::ZERO,
            4 => simd_ne(a, b),
            5 => simd_ge(a, b),
            6 => simd_gt(a, b),
            _ => i32x16::splat(-1),
        };
        simd_bitmask(r)
    }
}

